opam.opam:
opam-version: "1.2"
name: "ctypes-foreign"
version: "0.4.0"
maintainer: "yallop@gmail.com"
authors: "yallop@gmail.com"
tags: ["org:ocamllabs" "org:mirage"]
homepage: "https://github.com/ocamllabs/ocaml-ctypes"
bug-reports: "http://github.com/ocamllabs/ocaml-ctypes/issues"
post-messages: "This package requires libffi on your system" {failure}
depexts: [
  ["libffi-dev"] {"debian"}
  ["libffi-dev"] {"ubuntu"}
  ["libffi"] {"osx" & "homebrew"}
  ["libffi"] {"osx" & "macports"}
  ["libffi-devel"] {"centos"}
  ["libffi-devel"] {"oraclelinux"}
  ["libffi-devel"] {"fedora"}
  ["libffi-dev"] {"alpine"}
  ["libffi-devel"] {"opensuse"}
]
dev-repo: "git+http://github.com/ocamllabs/ocaml-ctypes.git"

opam-version: "1.2"
name: "ctypes"
version: "0.14.0"
maintainer: "yallop@gmail.com"
authors: "yallop@gmail.com"
license: "MIT"
tags: ["org:ocamllabs" "org:mirage"]
homepage: "https://github.com/ocamllabs/ocaml-ctypes"
doc: "http://ocamllabs.github.io/ocaml-ctypes"
bug-reports: "http://github.com/ocamllabs/ocaml-ctypes/issues"
depends: [
  "base-bytes"
  "integers"
  "ocamlfind" {build}
  "conf-pkg-config" {build}
  "lwt" {test & >= "2.4.7" & < "4.0.0"}
  "ounit" {test}
  "ctypes-foreign" {test}
  "conf-ncurses" {test}
]
depopts: ["ctypes-foreign" "mirage-xen"]
available: ocaml-version >= "4.01.0"
build: [
  [make "XEN=%{mirage-xen:enable}%" "libffi.config"]
    {ctypes-foreign:installed}
  ["touch" "libffi.config"] {!ctypes-foreign:installed}
  [make "XEN=%{mirage-xen:enable}%" "ctypes-base" "ctypes-stubs"]
  [make "XEN=%{mirage-xen:enable}%" "ctypes-foreign"]
    {ctypes-foreign:installed}
]
run-test: [make "test"]
install: [make "install" "XEN=%{mirage-xen:enable}%"]
remove: ["ocamlfind" "remove" "ctypes"]
dev-repo: "git+http://github.com/ocamllabs/ocaml-ctypes.git"

opam-version: "1.2"
name: "conf-sdl2"
version: "1"
maintainer: "blue-prawn"
authors: "Sam Lantinga"
license: "Zlib"
homepage: "http://libsdl.org/"
bug-reports: "https://github.com/ocaml/opam-repository/issues"
build: ["pkg-config" "sdl2"]
depexts: [
  ["sdl2-dev"] {"alpine"}
  ["sdl2"] {"arch"}
  ["libsdl2-dev"] {"debian"}
  ["SDL2-devel"] {"fedora"}
  ["sdl2"] {"freebsd"}
  ["sdl2"] {"osx" & "homebrew"}
  ["libsdl2-devel"] {"mageia"}
  ["sdl2"] {"openbsd"}
  ["sdl2"] {"opensuse"}
  ["libsdl2-dev"] {"ubuntu"}
]

opam-version: "1.2"
name: "conf-pkg-config"
version: "1.1"
maintainer: "unixjunkie@sdf.org"
authors: "Francois Berenger"
license: "GPL"
homepage: "http://www.freedesktop.org/wiki/Software/pkg-config/"
bug-reports: "https://github.com/ocaml/opam-repository/issues"
build: ["pkg-config" "--help"]
install:
  ["ln" "-s" "/usr/local/bin/pkgconf" "%{bin}%/pkg-config"] {os = "openbsd"}
remove: ["rm" "-f" "%{bin}%/pkg-config"] {os = "openbsd"}
post-messages:
  "conf-pkg-config: A symlink to /usr/local/bin/pkgconf has been installed in the OPAM bin directory (%{bin}%) on your PATH as 'pkg-config'. This is necessary for correct operation."
    {os = "openbsd"}
depexts: [
  ["pkg-config"] {"debian"}
  ["pkg-config"] {"ubuntu"}
  ["pkg-config"] {"archlinux"}
  ["pkgconfig"] {"fedora"}
  ["pkgconfig"] {"centos"}
  ["pkgconfig"] {"mageia"}
  ["pkgconfig"] {"rhel"}
  ["pkgconfig"] {"oraclelinux"}
  ["pkgconfig"] {"alpine"}
  ["devel/pkgconf"] {"freebsd"}
  ["devel/pkgconf"] {"openbsd"}
  ["pkg-config"] {"osx" & "homebrew"}
  ["pkgconf"] {"freebsd"}
]

